<script>
export default {
  data() {
    return {
      settingsShow: false,
      hudOn: false,
      h2On: false,
      showSettings: false,
      showNew: false,
      showEdit: false,
      showHelp: false,
      dark: false,
    };
  },
  computed: {
    done() {
      return this.$store.getters.doneTasks;
    },
  },
  methods: {
    hideSettings() {
      const settingsDiv = document.querySelector("#settings");
      const winWidth = window.innerWidth;
      if (winWidth <= 640) {
        if (this.settingsShow) {
          settingsDiv.style.width = "0";
          const setHUD = document.querySelector("#settings-hud");
          setHUD.style.transform = "scale(0)";
          setHUD.style.opacity = "0";
          setTimeout(() => {
            this.$store.dispatch("updateHeaderState", [5, "d"]);
          }, 50);
          setTimeout(() => {
            this.hudOn = false;
            this.settingsShow = false;
          }, 750);
        } else {
          this.$store.dispatch("updateHeaderState", [5, "m"]);
          settingsDiv.style.width = "30%";
          this.settingsShow = true;
          this.h2On = true;
          this.hudOn = true;
          setTimeout(() => {
            const setHUD = document.querySelector("#settings-hud");
            setHUD.style.transform = "scale(1)";
            setHUD.style.opacity = "1";
          }, 10);
        }
      } else {
        if (this.settingsShow) {
          settingsDiv.style.width = "0";
          settingsDiv.style.padding = "0";
          settingsDiv.style.paddingRight = "0";
          const setHUD = document.querySelector("#settings-hud");
          setHUD.style.transform = "scale(0)";
          setHUD.style.opacity = "0";
          setTimeout(() => {
            this.$store.dispatch("updateHeaderState", [5, "d"]);
          }, 50);
          setTimeout(() => {
            this.hudOn = false;
            this.settingsShow = false;
          }, 750);
        } else {
          this.$store.dispatch("updateHeaderState", [5, "m"]);
          settingsDiv.style.width = "10%";
          settingsDiv.style.padding = "1rem";
          this.settingsShow = true;
          this.h2On = true;
          this.hudOn = true;
          setTimeout(() => {
            const setHUD = document.querySelector("#settings-hud");
            setHUD.style.transform = "scale(1)";
            setHUD.style.opacity = "1";
          }, 10);
        }
      }
    },
    darkClick() {
      this.dark = !this.dark;
      this.$store.dispatch("switchDM");
    },
    doneClick() {
      this.$store.dispatch("switchDT");
    },
    helpClick() {
      if (this.$router.currentRoute.path !== "/") this.$router.replace("/");
    },
    windowResized() {
      const winWidth = window.innerWidth;
      const winHeight = window.innerHeight;
      const settingsDiv = document.querySelector("#settings");
      const rem = parseFloat(
        getComputedStyle(document.documentElement).fontSize
      );
      const headerDiv = document.querySelector("#app-header-new");
      const footerDiv = document.querySelector("#app-footer");
      let headerHeight, footerHeight;
      if (winWidth <= 640) {
        if (headerDiv && footerDiv) {
          headerHeight = headerDiv.clientHeight;
          footerHeight = footerDiv.clientHeight;
          settingsDiv.style.height = `${winHeight -
            headerHeight -
            footerHeight -
            1.5 * rem}px`;
        } else {
          settingsDiv.style.height = `${winHeight - 8 * rem}px`;
        }
      }
    },
  },
  mounted() {
    window.addEventListener("resize", this.windowResized);
    this.windowResized();
  },
  unmounted() {
    this.$store.dispatch("updateHeaderState", [1, "r"]);
    window.removeEventListener("resize", this.windowResized);
  },
};
</script>

<style>
#settings {
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 0;
  height: 90%;
  border-left: solid var(--col-0-t) 1px;
  margin-left: 2rem;
  transition: all 0.75s ease-in-out;
  background: var(--col-0-t);
  text-align: center;
  color: var(--col-0);
  z-index: 2;
}
.icon-settings {
  position: absolute;
  left: -2.5rem;
}

#settings-hud {
